package cn.edu.cqu.SpringcloudDemo.Model;import lombok.*;/** * @ author     ：alberto * @ date       ：Created in 2020/4/27 15:57 * @ description：数据交互JSON格式 */// lombok自动代码补全相关注解@Data@AllArgsConstructor@NoArgsConstructor@Setter@Getterpublic class MyJson<T> {    // 用于定义前后端数据交互的JSON格式,    // code代表操作结果(404代表not found之类的, 自定义),    // message代表附带消息, data代表返回给前端的数据对象    private int code;    private String message;    private T data;    // 可供失败消息返回    public MyJson(int code, String message) {        this(code, message, null);    }}