package cn.edu.cqu.SpringcloudDemo.Controller;import cn.edu.cqu.SpringcloudDemo.Model.MyJson;import cn.edu.cqu.SpringcloudDemo.Service.ItemService;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.*;import javax.annotation.Resource;/** * @ author     ：alberto * @ date       ：Created in 2020/4/28 11:28 * @ description：商品模块控制器 */// @RestController 注解标记的Controller返回的直接就是JSON字符串, 可以直接给前端使用// @Controller 注解标记的Controller返回的一般是String或ModelAndView对象,// 用来指明保存在resources文件夹中的html文件, 相当于服务器渲染并返回一个页面到浏览器@RestControllerpublic class ItemController {    @Resource    private ItemService itemService;    // 根据yml获取端口号    @Value("${server.port}")    private String serverPort;    // 响应请求    // 一般get开头的方法采用REST风格即为get, 其他需要上传数据的使用post    @GetMapping(value = "/item/getItem/{itemId}") // value对应前端传回请求    // 也可以使用 @RequestMapping(value = "/getUser", method = RequestMethod.GET)    @ResponseBody    public MyJson getUser(@PathVariable(value = "itemId") int itemId) {// 前端传入参数,        // 这里与UserController作区分, 采用另一种传参方式        return itemService.getItemById(itemId, serverPort);    }}